import React, { useEffect, useState } from 'react';
import { ErrorBoundary, useErrorHandler } from 'react-error-boundary';

function MyFallbackComponent(error) {
    return (
        <div role="alert">
            <p>Something went wrong:</p>
            <pre>{error.message}</pre>
            <button onClick={resetErrorBoundary}>Try again</button>
        </div>
    )
}

const Head = () => {

    const [india, setIndia] = useState('');
    const [japan, setJapan] = useState('');
    const handleError = useErrorHandler();

    useEffect(() => {
        setIndia('Hello World');
    }, [japan]);
    useEffect(() => {
        setJapan('Hello World');
    }, [india]);

    const handleClick = () => {
        try {
            if (india === 'Hello Japan') {
                throw new Error('Something went wrong!!')
            } else {
                setIndia('Hello India');
            }
        } catch (e) {
            handleError(e)
        }
    }

    return (
        <>
            <div>
                <h1>{india}</h1>
            </div>
            <ErrorBoundary FallbackComponent={MyFallbackComponent} onError={(error, errorInfo) => console.log({ error, errorInfo })}>
                <div>
                    <button onClick={handleClick}>India</button>
                    <button onClick={() => { setIndia('Hello Japan') }}>Japan</button>
                </div>
            </ErrorBoundary>
        </>
    )
}

export default Head
