import React, { useEffect, useState } from 'react';
import { ErrorBoundary, useErrorHandler } from 'react-error-boundary';

const MyFallbackComponent = (error) => {
    return <p>{error.Error}</p>
}

const Head = () => {

    const [india, setIndia] = useState('');
    const [japan, setJapan] = useState('');
    const handleError = useErrorHandler();

    useEffect(() => {
        setIndia('Hello World');
    }, [japan]);
    useEffect(() => {
        setJapan('Hello World');
    }, [india]);

    const handleClick = () => {
        try {
            if (india === 'Hello Japan') {
                setIndia('Hello India');
            } else {
                throw new Error('Something went wrong!!')
            }
        } catch (error) {
            handleError(error)
        }
    }

    return (
        <>
            <div>
                <h1>{india}</h1>
            </div>
            <div>
                <ErrorBoundary FallbackComponent={MyFallbackComponent} onReset={handleError}>
                    <button onClick={handleClick}>India</button>
                </ErrorBoundary>
                <button onClick={() => { setIndia('Hello Japan') }}>Japan</button>
            </div>
        </>
    )
}

export default Head
