import React, { useEffect, useState } from 'react';
import { ErrorBoundary, useErrorHandler } from 'react-error-boundary';

const MyFallbackComponent = () => {
    return <p>Something went wrong!</p>
}

const Head = () => {

    const [india, setIndia] = useState('');
    const [japan, setJapan] = useState('');

    useEffect(() => {
        setIndia('Hello World');
    }, [japan]);
    useEffect(() => {
        setJapan('Hello World');
    }, [india]);

    const handleClick = () => {
        try {
            if (india === 'Hello Japan') {
                setIndia('Hello India');
            } else {
                throw new Error('Something went wrong!!')
            }
        } catch (error) {
            useErrorHandler(error)
        }
    }

    return (
        <>
            <div>
                <h1>{india}</h1>
            </div>
            <div>
                <ErrorBoundary FallbackComponent={MyFallbackComponent} onError={(error, errorInfo) => console.log('Logging', error, errorInfo)}>
                    <button onClick={handleClick}>India</button>
                </ErrorBoundary>
                <button onClick={() => { setIndia('Hello Japan') }}>Japan</button>
            </div>
        </>
    )
}

export default Head
